KFunction.FunctionBody:
$entity_id=getEntityID();

$k2_tdm_entity=explode('_',$entity_id);
$k2_tdm_entity_size=sizeof($k2_tdm_entity);

switch ($k2_tdm_entity_size) {
		case 2 : //entity in the format of <environment>_<entity_id>
				$env = $k2_tdm_entity[0];
				$iid = $k2_tdm_entity[1];
				break;
		case 4 : //entity in the format of <environment>_<entity_id>_<task_name>_<timestamp>
				$env = $k2_tdm_entity[0];
				$iid = $k2_tdm_entity[1];
				$task_name = $k2_tdm_entity[2];
				$timestamp = $k2_tdm_entity[3];
				break;
}

yield array ($entity_id,$env,$iid,$task_name,$timestamp);
