KFunction.FunctionBody:
if ($value == null){
	return 'NULL';
}else{
	//if column type numeric
	if (in_array(trim(strtolower($columnType)),array('int','integer','real','double','decimal','numeric','bool','boolean','number'))){
		return $value;
	}else{
		return "'" . str_replace("'","''",$value) . "'";
	}
}
